# Electricity Bill# Part 1def bill_calculator( units ):	if units == 0:		cost = 0.0	elif units > 0 and units <= 5.0:		cost = 0.5	elif units > 5.0 and units < 50.0:		cost = 0.75	elif units >= 50.0:		cost = 1	else:		return "Input Invalid!"	totalBill = units * cost	return totalBill	# unitsConsumed = float(input( "Please enter the number of units consumed: "))# print("Your total bill is: " + str(bill_calculator(unitsConsumed)))#Sandwhich Order#Part 1.2def get_bread_list(sandwhich):    bread = list()    bread.append(sandwhich[0]) #add to list the first index in sandwhich    bread.append(sandwhich[-1]) #add last index     return breaddef get_filling_list(sandwhich):    filling = sandwhich[1: len(sandwhich) -1] #new list comprised of elements starting at second and going to second to last el    return filling hamSammy = ["Wheat bread", "Ham", "Cheese", "Wheat bread"]# print(get_bread_list(hamSammy))# print(get_filling_list(hamSammy))# Star Points# # Part 1.3def rock_paper_scissors(player1_move, player2_move):    if player1_move ==  player2_move:        print("Draw!")    if player1_move == "Rock" and player2_move == "Scissors":        print("Player1")    if player1_move == "Rock" and player2_move == "Paper":        print("Player2")    if player1_move == "Paper" and player2_move == "Scissors":        print("Player2")    if player1_move == "Paper" and player2_move == "Rock":        print("Player1")    if player1_move == "Scissors" and player2_move == "Paper":        print("Player1")    if player1_move == "Scissors" and player2_move == "Rock":        print("Player2")	# #player1_move = "Rock"# #player2_move = "Rock"# #rock_paper_scissors(player1_move, player2_move)# # # Part 1.4def extract_range_list(data):    minAndMax = data    del data[1: len(data) -1]    return minAndMax#myData = [3, 6, 8]#print(extract_range_list(myData))